:root {
  --white: #FFF;
  --smoke: #F8F8F8;
  --grey: #101010;
}

* {
  margin: 0;
  padding: 0;
}

*,
*::before,
*::after {
  box-sizing: inherit;
}

html {
  box-sizing: border-box;
  font-size: 62.5%;
}

body {
  font-family: "Open Sans", sans-serif;
  font-weight: 400;
  line-height: 1.6;
}

.header {
  width: 100%;
  min-height: 100vh;
}

.nav {
  display: flex;
  justify-content: center;
  align-items: center;
}

.nav__wrapper {
  width: 90%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding-top: 1rem;
}

.nav__wrapper__logo svg {
  height: 8rem;
  width: 8rem;
  fill: #000;
}

.nav__wrapper__items {
  display: flex;
  flex: 1;
  justify-content: space-evenly;
  list-style: none;
}

.nav__wrapper__item {
  padding: 1.5rem 3rem;
}

.nav__wrapper__link {
  text-decoration: none;
  font-size: 2.5rem;
  letter-spacing: 0.8rem;
  color: #000;
  font-weight: 700;
}

.hero {
  width: 100%;
  display: flex;
  justify-content: center;
}

.hero__container {
  width: 90%;
  min-height: calc(100vh - 95px);
  border: 1px solid black;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
}
.hero__container--left {
  background-color: var(--grey);
  display: flex;
  align-items: center;
  justify-content: flex-start;
  position: relative;
}
.hero__container--right {
  background-image: linear-gradient(to right bottom, red, orangered), url(232af875d6088667f0e2c0da9058f7e3.jpeg);
}

.user__icons {
  background: var(--smoke);
  list-style: none;
  padding: 1rem 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}
.user__icons__item {
  padding-top: 1rem;
}
.user__icons__link {
  text-decoration: none;
}

.user__icon {
  width: 2.5rem;
  height: 2.5rem;
  fill: var(--grey);
}

.text__container {
  display: flex;
  flex: 1;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  align-self: flex-start;
  padding: 2rem 2rem;
  background-color: var(--smoke);
  margin: 4rem 1rem 2rem 1rem;
  border-radius: 5px;
  -webkit-box-shadow: 10px 4px 32px -19px #fff5ff;
  -moz-box-shadow: 10px 4px 32px -19px #fff5ff;
  box-shadow: 10px 4px 32px -19px #fff5ff;
  -webkit-clip-path: polygon(50% 0%, 100% 8%, 100% 91%, 50% 100%, 0 93%, 0 8%);
  clip-path: polygon(50% 0%, 100% 8%, 100% 91%, 50% 100%, 0 93%, 0 8%);
}
.text__container__h1 {
  color: var(--grey);
  font-size: 3.2rem;
  letter-spacing: 0.8rem;
  text-shadow: 2px 2px 4px #000;
  text-align: center;
  line-height: 1;
  padding-bottom: 1.5rem;
  border-bottom: 2px solid #000;
  width: 100%;
  text-align: center;
  font-weight: 600;
}
.text__container__h3 {
  width: 100%;
  text-align: center;
  font-size: 2rem;
  letter-spacing: 0.4rem;
  padding-top: 1rem;
  margin-top: 1rem;
  border-top: 2px solid #000;
  font-weight: 500;
  text-shadow: 1px 1px 2px #000;
  color: var(--grey);
}
.text__container__p {
  font-size: 1.8rem;
  letter-spacing: 0.3rem;
  line-height: 1.5;
  padding: 4rem 1rem;
  font-weight: 100;
  color: var(--grey);
}

.text__vertical__h3 {
  writing-mode: vertical-rl;
  background-color: var(--smoke);
  letter-spacing: 1rem;
  color: var(--grey);
  font-weight: 500;
  justify-self: flex-end;
  font-size: 1.6rem;
  border-right: 1px solid #000;
  text-shadow: 1px 1px 2px #000;
  line-height: 1;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 2rem 1rem;
}

.contact__info {
  position: absolute;
  bottom: 5%;
  width: 100%;
  display: flex;
  justify-content: space-evenly;
  align-items: center;
}
.contact__info__link {
  text-decoration: none;
  color: var(--smoke);
  font-size: 1.7rem;
  display: flex;
  align-items: center;
  justify-content: center;
}
.contact__info__icon {
  width: 2.5rem;
  height: 2.5rem;
  fill: var(--smoke);
  margin-right: 1.1rem;
}
